dt-example.js:
$(document).ready(function() {
    new DateTime(document.getElementById('test'), {
		i18n: {
			previous: 'Précédent',
			next:     'Premier',
			months:   [ 'Janvier', 'Février', 'Mars', 'Avril', 'Mai', 'Juin', 'Juillet', 'Août', 'Septembre', 'Octobre', 'Novembre', 'Décembre' ],
			weekdays: [ 'Dim', 'Lun', 'Mar', 'Mer', 'Jeu', 'Ven', 'Sam' ]
		}
	});
});
