dt-example.js:
$(document).ready(function() {
	new DateTime($('#inputDate'), {
		format: 'MMMM Do YYYY'
	});
	new DateTime($('#inputTime'), {
		format: 'h:mm a'
	});

	$('#appointment').on('submit', function (e) {
		e.preventDefault();

		alert(
			'Making an appointment for ' +
			$('#inputName').val() +
			' (' + $('#inputPhone').val() + ') on ' +
			$('#inputDate').val() + ' at ' +
			$('#inputTime').val()
		);
	});
});
